number_column.script:
function action(draginfo)
	local rActor = ActorManager.getActor("npc", window.getDatabaseNode());
	ActionInit.performRoll(draginfo, rActor);

	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)
	return action();
end

basicnumber.script:
function action(draginfo)
	local rActor = ActorManager.getActor("npc", window.getDatabaseNode());
	ActionSpell.performSaveRoll(draginfo, rActor, self.target[1]);

	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x, y)
	return action();
end

function action(draginfo)
	local rActor = ActorManager.getActor("npc", window.getDatabaseNode());
	ActionAbility.performRoll(draginfo, rActor, self.target[1]);

	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x, y)
	return action();
end

buttoncontrol.script:
function onValueChanged()
	if window.toggleDetail then
		window.toggleDetail();
	end
end

windowlist.script:
function onInit()
	registerMenuItem(Interface.getString("list_menu_createitem"), "insert", 5);
end

function onMenuSelection(selection)
	if selection == 5 then
		self.addEntry(true);
	end
end

function onListChanged()
	update();
end

function update()
	local bEditMode = (window.list_iedit.getValue() == 1);
	for _,w in pairs(getWindows()) do
		w.idelete.setVisibility(bEditMode);
	end
end

function addEntry(bFocus)
	return createWindow();
end
