number_ps_linked.script:
function onInit()
	super.onInit();
	if not User.isHost() then
		setEnabled(false);
	end
end

function action(draginfo)
	if not User.isHost() then
		return false;
	end

	local rActor = ActorManager.getActor("pc", window.link.getTargetDatabaseNode());

	local nTargetDC = DB.getValue("partysheet.abilitydc", 0);
	if nTargetDC == 0 then
		nTargetDC = nil;
	end

	local bSecretRoll = (DB.getValue("partysheet.hiderollresults", 0) == 1);

	ActionAbility.performRoll(draginfo, rActor, self.target[1], nTargetDC, bSecretRoll);
	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)
	return action();
end

function onInit()
	super.onInit();
	if not User.isHost() then
		setEnabled(false);
	end
end

function action(draginfo)
	if not User.isHost() then
		return false;
	end

	local rActor = ActorManager.getActor("pc", window.link.getTargetDatabaseNode());

	local nTargetDC = DB.getValue("partysheet.savedc", 0);
	if nTargetDC == 0 then
		nTargetDC = nil;
	end

	local bSecretRoll = (DB.getValue("partysheet.hiderollresults", 0) == 1);

	ActionSpell.performSaveRoll(draginfo, rActor, target[1], nTargetDC, bSecretRoll);
	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)	
	return action();
end

function onInit()
	super.onInit();
	if not User.isHost() then
		setEnabled(false);
	end
end

function action(draginfo)
	if not User.isHost() then
		return false;
	end

	if skill then
		local rActor = ActorManager.getActor("pc", window.link.getTargetDatabaseNode());
		local nValue;

		local sSkill = skill[1];
		local sSkillLookup = sSkill;
		local sSubSkill = nil;
		if subskill then
			sSubSkill = subskill[1];
			sSkill = sSkill .. " (" .. sSubSkill .. ")";
		end

		nValue = CharManager.getSkillValue(rActor, sSkillLookup, sSubSkill);

		local nTargetDC = DB.getValue("partysheet.skilldc", 0);
		if nTargetDC == 0 then
			nTargetDC = nil;
		end

		local bSecretRoll = (DB.getValue("partysheet.hiderollresults", 0) == 1);

		ActionSkill.performRoll(draginfo, rActor, sSkill, nValue, nil, nTargetDC, bSecretRoll);
		return true;
	end

	return false;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)	
	return action();
end

genericcontrol.script:
function onClickDown()
	if User.isHost() then
		return true;
	end
end

function onClickRelease()
	if User.isHost() then
		window.link.activate();
		return true;
	end
end

function onInit()
	update();
end

function update()
	local _,sCharNode = window.link.getValue();
	local sCharNodeName = sCharNode:match("%.([%w-]+)$");
	if sCharNodeName then
		setIcon("portrait_" .. sCharNodeName .. "_charlist");
	else
		setIcon();
	end
end

simplestring.script:
function onClickDown()
	return true;
end

function onClickRelease()
	window.clicked();
end
